import 'bootstrap/dist/css/bootstrap.min.css';
import './App.css';
// import Menu from './components/menu';
import React ,{useState} from 'react';
// import Categories from './components/categories';
// import Navbar from './components/navbar'
import './styles/warning.css'
// import Cart from './components/cart.jsx'
import Adminlogin from './components/adminlogin.jsx';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import Home from './components/homepage';
import Chef from './components/chef.jsx';

function App() {

  const [orderisdone, setorderisdone] = useState(false);
  const [customerid,setCustomerId] = useState("");

  const orderdone = async(id) => {
    console.log("reach till app.jsx ")
    const response = await fetch(`http://localhost:3001/mysite/orders/order/${id}`, {
      method: "PATCH",
      headers: {
        "Content-Type": "application/json"
      },
      body: JSON.stringify({ ispending: false}),
    })
    const data = await response.json()
    console.log(data.data)
    setorderisdone(true);
    setCustomerId(id);
  }

  return (
    <React.Fragment>
      <Router>
        <Routes>
          <Route path='/' element={<Home orderisdone={orderisdone} customerid={customerid} setorderisdone={setorderisdone}/>} />
          <Route path="/adminlogin" element={<Adminlogin />} />
          <Route path="/chef" element={<Chef orderdone={orderdone}/>} />
        </Routes>
      </Router>
    </React.Fragment>

  );
}

export default App;
