import React from 'react'
import { useEffect,useState } from 'react'

 const Chef = () => {

    const [orders, setOrders] = useState([]);
    useEffect(() => {
        const fetchdata = async () => {
            const response = await fetch("http://localhost:3001/mysite/orders/order/pending", {
                method: "GET",
                headers: {
                    "Content-Type": "application/json"
                }
            })
            const data = await response.json()
            console.log(data)
            // console.log("middle")
            // console.log({data:data})
            setOrders(data.data)
        }
        fetchdata();
    }, [])

  return (
    <main className="product-space">
            <div className="container">
                <div className="row">
                    {orders?.map((Items) => {
                        const { _id, username, phone, ispending, items , date } = Items;
                        return (
                            <div key={_id} className="col-md-6">
                                <article className="main-div">
                                    {/* <div className="main-img">
                                        <div>{items[0].title}</div>
                                    </div> */}
                                    <div className="content">
                                        <div className="title">
                                            <h4>Name:{username}</h4>
                                            <h6>Phone:{phone}</h6>
                                        </div>
                                        <p>{date}</p>
                                        
                                        {
                                            items.map((item)=>{
                                                const { _id, title, img, price , amount } = item;
                                                return(
                                                <div className="cart_box" key={_id}>
                                                    <div className="cart_img">
                                                        <img src={img} alt=""/>
                                                        <p>{title}</p>
                                                    </div>
                                                    <div className="cart_price">
                                                        <p>{price}</p>
                                                    </div>
                                                    <div className="cart_amount">
                                                        <p>{amount}</p>
                                                    </div>
                                                    <div className="cart_total">
                                                        <p>{price * amount}</p>
                                                    </div>
                                                </div>
                                                )})
                                        }
                                        
                                         
                                        {
                                            ispending ?
                                                <div>
                                                    <button className="btn btn-warning" onClick={""}>isPending</button>
                                                </div>
                                                :
                                                <div>
                                                    <button className="btn btn-success">Done</button>
                                                </div>
                                        }

                                    </div>
                                </article>
                            </div>
                        );
                    })}
                </div>
            </div>
        </main>
  )
}

export default Chef
