import React , {useState} from 'react'

 const Addrecipe = () => {
    const [user, setUser] = useState({title : "" , price : "" , desc : "" , img : "" , category : ""})
    const onChangeUser = (e) => {
        setUser({...user , [e.target.name] : e.target.value})
    }
    const onSubmit = async (e) => {
        e.preventDefault();
        console.log(user)

        const response = await fetch("http://localhost:3001/mysite/menu/addmenu", {
            method: "POST",
            headers: {
                "Content-Type": "application/json"
            },
            body: JSON.stringify(user)
        })
    }
  return (
    <>
        <form onSubmit={onSubmit}>
            <h1>title is {user.title}</h1>
            <h1>price is {user.price}</h1>
            <h1>desc is {user.desc}</h1>
            <h1>img is {user.img}</h1>
            <h1>category is {user.category}</h1>
            <input type="text" value={user.title} placeholder="title" name="title" onChange={onChangeUser} />
            <input type="text" value={user.price} placeholder="price" name="price" onChange={onChangeUser} />
            <input type="text" value={user.desc} placeholder="desc" name="desc" onChange={onChangeUser} />
            <input type="text" value={user.img} name="img" placeholder="img" onChange={onChangeUser} />
            <input type="text" value={user.category} name="category" placeholder="category" onChange={onChangeUser} />
            <button type="submit">submit</button>
        </form>
    </>
  )
}

export default Addrecipe
