import 'bootstrap/dist/css/bootstrap.min.css';
import './App.css';
import Menu from './components/menu';
import React, { useEffect, useState } from 'react';
import items from './components/data'
import Categories from './components/categories';
import Navbar from './components/navbar'
import './styles/warning.css'
import Cart from './components/cart.jsx'
import Adminlogin from './components/adminlogin.jsx';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import Home from './components/homepage';


const allcategories = ['all', ...new Set(items.map((item) => item.category))]

function Homepage() {

  useEffect(() => {
    const fetchdata = async () => {
      const response = await fetch("http://localhost:3001/mysite/menu/viewmenu", {
        method: "GET",
        headers: {
          "Content-Type": "application/json"
        }

      }) // fetch data from the server
      const data = await response.json()
      console.log(data)
      setMenuItems(data.data)
    }
    fetchdata();
  }, []);


  const [menuItems, setMenuItems] = useState([]);
  const [categories, setcategories] = useState(allcategories);
  const [cart, setCart] = useState([]);
  const [warning, setWarning] = useState(false);
  const [show, setShow] = useState(true);

  const filterItems = (category) => {
    if (category === 'all') {
      return setMenuItems(items);
    }
    const newItems = items.filter((item) => item.category === category)
    return setMenuItems(newItems)

  }

  const handleclick = (item) => {
    let ispresent = false;
    cart.forEach((product) => {
      // eslint-disable-next-line eqeqeq
      if (item._id == product._id)
        ispresent = true;
    })
    if (ispresent) {
      setWarning(true);
      setTimeout(() => {
        setWarning(false);
      }, 1500)
      return;

    }
    setCart([...cart, item])
    console.log(item)
  }

  const handleChange = (item, d) => {
    console.log(item)
    console.log(d)
    let ind = -1;
    cart.forEach((data, index) => {
      if (data._id === item._id)
        ind = index;
    });
    const tempArr = cart;
    tempArr[ind].amount += d;

    if (tempArr[ind].amount === 0)
      tempArr[ind].amount = 1;
    setCart([...tempArr])
  }

 function homepage() {
        return (
            <React.Fragment>
                <Router>
                    <div className="container">
                        <Navbar size={cart.length} setShow={setShow} />
                        <div className='row'>
                            <div className='col-md-12'>

                                <Routes>
                                    <Route path='/' element={<Home />} />
                                    <Route path="/adminlogin" element={<Adminlogin />} />
                                </Routes>

                                {show ? <div>
                                    <h2 className='text-center py-5'>Our Menu</h2>

                                    <Categories filterItems={filterItems} categories={categories} />

                                    <Menu items={menuItems} handleclick={handleclick} />
                                </div>
                                    : <Cart cart={cart} setCart={setCart} handleChange={handleChange} />}
                                {warning && <h3 className="warning">Item is already in cart</h3>}
                            </div>

                        </div>

                    </div>
                </Router>
            </React.Fragment>
        );
    }
}

export default Home;
