import React , {useState} from 'react'

 const Addrecipe = () => {
    const [user, setUser] = useState({username : "" , password : ""})
    const onChangeUser = (e) => {
        setUser({...user , [e.target.name] : e.target.value})
    }
  return (
    <>
        <form>
            <h1>username is {user.username}</h1>
            <h1>price is {user.password}</h1>
            <input type="text" value={user.username} name="username" onChange={onChangeUser} />
            <input type="password" value={user.password} name="password" onChange={onChangeUser} />
        </form>
    </>
  )
}

export default Addrecipe
