import React from 'react'
import { useEffect,useState } from 'react'

 const Chef = () => {

    const [orders, setOrders] = useState([]);
    useEffect(() => {
        const fetchdata = async () => {
            const response = await fetch("http://localhost:3001/mysite/orders/order/pending", {
                method: "GET",
                headers: {
                    "Content-Type": "application/json"
                }
            })
            const data = await response.json()
            console.log(data)
            console.log("middle")
            console.log(data)
            setOrders(data)
        }
        fetchdata();
    }, [])

  return (
    <main className="product-space">
            <div className="container">
                <div className="row">
                    {orders.map((Items) => {
                        const { _id, username, phone, ispending, items , date } = Items;
                        return (
                            <div key={_id} className="col-md-6">
                                <article className="main-div">
                                    <div className="main-img">
                                        {/* <div>{items}</div> */}
                                    </div>
                                    <div className="content">
                                        <div className="title">
                                            <h4>{username}</h4>
                                            <h6>${phone}</h6>
                                        </div>
                                        <p>{date}</p>
                                        {
                                            ispending ?
                                                <div>
                                                    <button className="btn btn-warning" onClick={""}>isp</button>
                                                </div>
                                                :
                                                <div>
                                                    <button className="btn btn-success" onClick={""}>isPending</button>
                                                </div>
                                        }

                                    </div>
                                </article>
                            </div>
                        );
                    })}
                </div>
            </div>
        </main>
  )
}

export default Chef
