import React , {useState} from 'react'

 const Addrecipe = () => {
    const [user, setUser] = useState({title : "" , price : "" , })
    const onChangeUser = (e) => {
        setUser({...user , [e.target.name] : e.target.value})
    }
    const onSubmit = async (e) => {
        e.preventDefault();
        console.log(user)

        const response = await fetch("http://localhost:3001/mysite/menu/addmenu", {
            method: "POST",
            headers: {
                "Content-Type": "application/json"
            },
            body: JSON.stringify(user)
        })
    }
  return (
    <>
        <form onSubmit={onSubmit}>
            <h1>title is {user.title}</h1>
            <h1>price is {user.price}</h1>
            <input type="text" value={user.title} name="title" onChange={onChangeUser} />
            <input type="price" value={user.price} name="price" onChange={onChangeUser} />
            <button type="submit">submit</button>
        </form>
    </>
  )
}

export default Addrecipe
